dataSet.query:
SELECT ALLOWED BEGINOFPERIOD(AnswersSliceLast.Visit.Date, DAY) AS Day, AnswersSliceLast.Outlet, AnswersSliceLast.SKU, CASE WHEN AnswersSliceLast.Answer = "Да" OR AnswersSliceLast.Answer = "Yes" THEN 1 WHEN AnswersSliceLast.Answer = "Нет" OR AnswersSliceLast.Answer = "No" THEN 0 END AS Answer, AnswersSliceLast.Visit.SR AS SR INTO Fact FROM InformationRegister.Answers.SliceLast AS AnswersSliceLast WHERE AnswersSliceLast.QuestionAssignment = VALUE(Enum.SKUQuestions.Available) {WHERE (AnswersSliceLast.Visit.Date >= &BeginDate), (AnswersSliceLast.Visit.Date <= &EndDate)} ;
//////////////////////////////////////////////////////////////////////////////// SELECT ALLOWED AssortmentMatrixOutletsSliceLast.Outlet, AssortmentMatrixSKUsSliceLast.SKU, MAX(AssortmentMatrixSKUsSliceLast.BaseUnitQty) AS BaseUnitQty, AssortmentMatrixPeriods.BeginDate, AssortmentMatrixPeriods.EndDate INTO Plan FROM InformationRegister.AssortmentMatrixSKUs.SliceLast AS AssortmentMatrixSKUsSliceLast FULL JOIN Catalog.AssortmentMatrix.Periods AS AssortmentMatrixPeriods ON AssortmentMatrixSKUsSliceLast.AssortmentMatrix = AssortmentMatrixPeriods.Ref AND AssortmentMatrixSKUsSliceLast.UUID = AssortmentMatrixPeriods.UUID FULL JOIN InformationRegister.AssortmentMatrixOutlets.SliceLast AS AssortmentMatrixOutletsSliceLast ON AssortmentMatrixSKUsSliceLast.AssortmentMatrix = AssortmentMatrixOutletsSliceLast.AssortmentMatrix AND AssortmentMatrixSKUsSliceLast.UUID = AssortmentMatrixOutletsSliceLast.UUID WHERE NOT AssortmentMatrixOutletsSliceLast.OutletStatus = VALUE(Enum.ValueTableRowStatuses.Deleted) AND NOT AssortmentMatrixSKUsSliceLast.SKUStatus = VALUE(Enum.ValueTableRowStatuses.Deleted) {WHERE (AssortmentMatrixPeriods.BeginDate >= &BeginDate), (AssortmentMatrixPeriods.EndDate >= &EndDate)}
GROUP BY AssortmentMatrixPeriods.EndDate, AssortmentMatrixPeriods.BeginDate, AssortmentMatrixOutletsSliceLast.Outlet, AssortmentMatrixSKUsSliceLast.SKU ;
//////////////////////////////////////////////////////////////////////////////// SELECT ALLOWED Fact.Day, ISNULL(Fact.Outlet, Plan.Outlet) AS Outlet, ISNULL(Fact.SKU, Plan.SKU) AS SKU, Fact.Answer AS Answer, CASE WHEN Plan.BaseUnitQty IS NULL THEN 0 ELSE 1 END AS BaseUnitQty, Fact.SR INTO PlanFact FROM Fact AS Fact FULL JOIN Plan AS Plan ON Fact.Outlet = Plan.Outlet AND Fact.SKU = Plan.SKU AND Fact.Day >= Plan.BeginDate AND Fact.Day <= Plan.EndDate ;
//////////////////////////////////////////////////////////////////////////////// SELECT ALLOWED PlanFact.Day, PlanFact.Outlet.Distributor, PlanFact.Outlet.Class, PlanFact.Outlet.Type, PlanFact.Outlet, PlanFact.SKU.Brand AS Brand, PlanFact.SKU.Owner AS SKUGroup, PlanFact.SKU, PlanFact.Answer, PlanFact.BaseUnitQty, CASE WHEN PlanFact.BaseUnitQty = 1 AND PlanFact.Answer = 1 THEN 1 WHEN PlanFact.BaseUnitQty = 1 AND PlanFact.Answer = 0 THEN 0 ELSE NULL END AS PlanFactQty, MAX(TerritoryOutlets.Ref) AS Territory, MAX(TerritoryOutlets.Ref.Owner) AS Region, PlanFact.SR INTO PlanFactTerritory FROM PlanFact AS PlanFact INNER JOIN Catalog.Territory.Outlets AS TerritoryOutlets ON PlanFact.Outlet = TerritoryOutlets.Outlet WHERE NOT PlanFact.Day IS NULL
GROUP BY PlanFact.Day, PlanFact.Outlet, PlanFact.SKU, PlanFact.Outlet.Distributor, PlanFact.Outlet.Class, PlanFact.Outlet.Type, PlanFact.SKU.Brand, PlanFact.SKU.Owner, PlanFact.Answer, PlanFact.BaseUnitQty, PlanFact.SR ;
//////////////////////////////////////////////////////////////////////////////// SELECT ALLOWED PlanFactTerritory.Day, BEGINOFPERIOD(PlanFactTerritory.Day, MONTH) AS Month, CASE WHEN MONTH(PlanFactTerritory.Day) IN (2, 4, 6, 8, 10, 12) THEN BEGINOFPERIOD(DATEADD(PlanFactTerritory.Day, MONTH, -1), MONTH) ELSE BEGINOFPERIOD(PlanFactTerritory.Day, MONTH) END AS Month2, BEGINOFPERIOD(PlanFactTerritory.Day, QUARTER) AS Quarter, PlanFactTerritory.OutletDistributor, PlanFactTerritory.OutletClass, PlanFactTerritory.OutletType, PlanFactTerritory.Outlet, PlanFactTerritory.Brand, PlanFactTerritory.SKUGroup, PlanFactTerritory.SKU, PlanFactTerritory.Answer, PlanFactTerritory.BaseUnitQty, PlanFactTerritory.PlanFactQty, PlanFactTerritory.Territory, PlanFactTerritory.Region, PlanFactTerritory.SR FROM PlanFactTerritory AS PlanFactTerritory
